    private void btnCalculateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalculateActionPerformed
        USTax taxInstance = USTax.getInstance();
        float price = Float.parseFloat(txtPrice.getText());
        float tax = taxInstance.TotalTax(price);
        float totalPrice = taxInstance.TotalPrice(price);
        txtTaxCalculator.setText(String.valueOf(tax));
        txtPriceFinal.setText(String.valueOf(totalPrice));
    }//GEN-LAST:event_btnCalculateActionPerformed

