    public void readData() {
        value = Float.parseFloat(txtValue.getText());
    }

    public void operation() {
        Tax tax = Tax.getInstance();
        float calculatedValue = tax.computeSalesTotal(value);
        txtValueResult.setText(String.valueOf(calculatedValue));
        txtIva.setText(String.valueOf(tax.getTax()));
    }


