    private void btnAccessActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAccessActionPerformed
        if (validateFields()) {
            openAlert();
        }
    }//GEN-LAST:event_btnAccessActionPerformed

    private boolean validateFields() {
        String username = txtUser.getText().trim();
        String password = new String(txpPasword.getPassword());

        if (username.isEmpty() || password.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Please enter your username and password.", "Error", JOptionPane.ERROR_MESSAGE);
            return false;
        }
        return true;
    }

    private void openAlert() {
        String input = JOptionPane.showInputDialog(this, "Enter the new IVA:", "Change IVA", JOptionPane.INFORMATION_MESSAGE);

        if (input != null && !input.isEmpty()) {
            try {
                float newIva = Float.parseFloat(input);

                Tax taxInstance = Tax.getInstance();
                taxInstance.updateTax(newIva);

                FrmCalculator frmCalculator = new FrmCalculator();
                frmCalculator.setVisible(true);

                this.dispose();
            } catch (NumberFormatException ex) {
                JOptionPane.showMessageDialog(this, "Invalid input. Please enter a valid number.", "Error", JOptionPane.ERROR_MESSAGE);
            }
        } else {
            JOptionPane.showMessageDialog(this, "Please enter a valid number.", "Error", JOptionPane.ERROR_MESSAGE);
        }
    }

