    private void btnPrepareActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnPrepareActionPerformed
        prepareTea();
        prepareCoffee();
    }//GEN-LAST:event_btnPrepareActionPerformed
    private void prepareTea() {
        Tea tea = new Tea("Tea Recipe", "Boiling Water for Tea", "Pouring Tea", "Brewing Tea", "Adding Tea Condiments");
        displayMessage("Tea", tea);
    }

    private void prepareCoffee() {
        Coffe coffe = new Coffe("Coffee Recipe", "Boiling Water for Coffee", "Pouring Coffee", "Brewing Coffee", "Adding Coffee Condiments");
        displayMessage("Coffee", coffe);
    }

    private void displayMessage(String beverageName, CaffeineBeverage beverage) {
        if (beverage instanceof Tea) {
            Tea tea = (Tea) beverage;
            String message = beverageName + ":\n"
                    + beverage.getRecipe() + "\n"
                    + beverage.getBoilWater() + "\n"
                    + tea.getBrew() + "\n"
                    + tea.getCondiments() + "\n\n";
            appendToRecipeArea(message);
        } else if (beverage instanceof Coffe) {
            Coffe coffee = (Coffe) beverage;
            String message = beverageName + ":\n"
                    + beverage.getRecipe() + "\n"
                    + beverage.getBoilWater() + "\n"
                    + coffee.getBrew() + "\n"
                    + coffee.getCondiments() + "\n\n";
            appendToRecipeArea(message);
        }
    }

    private void appendToRecipeArea(String message) {
        txaRecipe.append(message);
    }

