    private void btnPrepareDrinkActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnPrepareDrinkActionPerformed
        Coffee coffee = new Coffee();
        StringBuilder preparationSteps = new StringBuilder();

        preparationSteps.append("Preparing a Tea:\n");
        preparationSteps.append("--------------------\n");

        coffee.prepareBeverage(); 
        preparationSteps.append(getPreparationSteps(coffee));

        txaDrink.setText(preparationSteps.toString());
    }//GEN-LAST:event_btnPrepareDrinkActionPerformed

    private String getPreparationSteps(BeverageTemplate beverage) {
        StringBuilder steps = new StringBuilder();
        steps.append("Boiling water...\n");
        beverage.boilWater();
        steps.append("Brewing...\n");
        beverage.brew();
        steps.append("Pouring into cup...\n");
        beverage.pourInCup();
        steps.append("Adding condiments...\n");
        beverage.addCondiments();
        return steps.toString();
    }

